mapper.select:
SELECT id, table_id, company_name, vessel_name, vessel_voyage, schedule_type, inout_type, event_date, area_code, area_name, from_port, to_port, from_date, to_date FROM TB_SCHEDULE WHERE 1=1
AND id =#{id}
AND company_name LIKE '%'+#{company_name}+'%'
AND inout_type = #{inout_type}
AND from_port = #{from_port}
AND to_port = #{to_port}

mapper.insert:
INSERT INTO TB_SCHEDULE ( 
	table_id,
	vessel_name,			
	vessel_voyage,
	inout_type,
	event_date,			
	from_port,			
	from_date,
	to_port,
	to_date
) VALUES 

( #{schedule.table_id}
, #{schedule.vessel_name}
, #{schedule.vessel_voyage}
, #{schedule.inout_type}
, #{schedule.event_date}
, #{schedule.from_port}
, #{schedule.from_date}
, #{schedule.to_port}
, #{schedule.to_date})
